import { Box } from "@chakra-ui/layout";
import { useState } from "react";
import Chatbox from "../components/Chatbox";
import MyChats from "../components/MyChats";
import SideDrawer from "../components/miscellaneous/SideDrawer";
import { ChatState } from "../Context/ChatProvider";

const Chatpage = () => {
  const [fetchAgain, setFetchAgain] = useState(false);
  const { user } = ChatState();

  return (
    <div style={{ width: "100%" }}>
      {user && <SideDrawer />}
      <Box d="flex" justifyContent="space-between" w="100%" h="91.5vh" p="10px">
        {user && <MyChats fetchAgain={fetchAgain} />}
        {user && (
          <div dangerouslySetInnerHTML={{ __html: `
            <!DOCTYPE html>
            <html lang="en">
            <head>
                <meta charset="UTF-8">
                <meta name="viewport" content="width=device-width, initial-scale=1.0">
                <title>ChatGPT Tone Changer</title>
                <style>
                    body {
                        font-family: Arial, sans-serif;
                        margin: 0;
                        padding: 0;
                        background-image: url('https://source.unsplash.com/1600x900/?clouds');
                        background-size: cover;
                        background-repeat: no-repeat;
                        background-attachment: fixed;
                    }
                    /* Other styles omitted for brevity */
                </style>
            </head>
            <body>
                <div class="container">
                    <h1>ChatGPT Tone Changer</h1>
                    <div class="input-container">
                        <textarea id="userText" rows="4" placeholder="Type your message here..."></textarea>
                        <select id="toneSelect">
                            <option value="professional">Professional</option>
                            <option value="informal">Informal</option>
                            <option value="funny">Funny</option>
                        </select>
                        <button onclick="changeTone()">Change Tone</button>
                    </div>
                    <div id="chatWindow" class="chat-window"></div>
                </div>
                <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
                <script>
                    function changeTone() {
                        var userText = $("#userText").val();
                        var selectedTone = $("#toneSelect").val();
                        // Send request to your backend API with userText and selectedTone
                        $.ajax({
                            url: 'your_backend_api_url',
                            type: 'POST',
                            contentType: 'application/json',
                            data: JSON.stringify({
                                text: userText,
                                tone: selectedTone
                            }),
                            success: function(response) {
                                $("#chatWindow").html('<p>' + response.modifiedText + '</p>');
                            },
                            error: function(xhr, status, error) {
                                console.error('Error:', error);
                            }
                        });
                    }
                </script>
            </body>
            </html>
          `}} />
        )}
        {user && (
          <Chatbox fetchAgain={fetchAgain} setFetchAgain={setFetchAgain} />
        )}
      </Box>
    </div>
  );
};

export default Chatpage;
