import { Box } from "@chakra-ui/layout";
import { useState } from "react";
import Chatbox from "../components/Chatbox";
import MyChats from "../components/MyChats";
import SideDrawer from "../components/miscellaneous/SideDrawer";
import SlideEx from "../components/miscellaneous/SlideEx";

import { ChatState } from "../Context/ChatProvider";

const Chatpage = () => {
  const [fetchAgain, setFetchAgain] = useState(false);
  const { user } = ChatState();

  return (
    <div style={{ width: "100%" }}>
      {user && (
        <Box d="flex" h="91.5vh" p="10px">
          {/* SideDrawer aligned to the left */}
          <SideDrawer w="250px" mr="20px">
            {/* SideDrawer content */}
          </SideDrawer>

          {/* Main content area with Chatbox and SlideEx */}
          <Box d="flex" flexDirection="column" flexGrow={1}>
            <Chatbox fetchAgain={fetchAgain} setFetchAgain={setFetchAgain} />
            <SlideEx />
          </Box>
        </Box>
      )}
    </div>
  );
};

export default Chatpage;
