import { Avatar } from "@chakra-ui/avatar";
import { Tooltip } from "@chakra-ui/tooltip";
import ScrollableFeed from "react-scrollable-feed";
import {
  isLastMessage,
  isSameSender,
  isSameSenderMargin,
  isSameUser,
} from "../config/ChatLogics";
import { ChatState } from "../Context/ChatProvider";

const ScrollableChat = ({ messages }) => {
  const { user } = ChatState();

  return (
    <ScrollableFeed>
      {messages.map((message, index) => {
        const { _id, content, sender } = message;
        const sameSender = isSameSender(messages, message, index, user._id);
        const lastMessage = isLastMessage(messages, index, user._id);
        const sameUser = isSameUser(messages, message, index, user._id);
        const sameSenderMargin = isSameSenderMargin(messages, message, index, user._id);

        return (
          <div style={{ display: "flex" }} key={_id}>
            {sameSender || lastMessage ? (
              <Tooltip label={sender.name} placement="bottom-start" hasArrow>
                <Avatar
                  mt="7px"
                  mr={1}
                  size="sm"
                  cursor="pointer"
                  name={sender.name}
                  src={sender.pic}
                />
              </Tooltip>
            ) : null}
            <span
              style={{
                backgroundColor: sender._id === user._id ? "#BEE3F8" : "#B9F5D0",
                marginLeft: sameSenderMargin,
                marginTop: sameUser ? 3 : 10,
                borderRadius: "20px",
                padding: "5px 15px",
                maxWidth: "75%",
              }}
            >
              {content}
            </span>
          </div>
        );
      })}
    </ScrollableFeed>
  );
};

export default ScrollableChat;
