import React, { useState } from 'react';
import { Box, Button } from '@chakra-ui/react';

const SlideEx = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSlide = () => {
    setIsOpen(!isOpen);
  };

  const changeTone = () => {
    // Simulate sending request (replace with actual API call)
    const userText = "Your message here...";
    const selectedTone = "informal";
    const modifiedText = `The tone of your message has been changed to ${selectedTone}`;
    return modifiedText;
  };

  return (
    <>
      <Button onClick={toggleSlide}>Click Me</Button>
      {isOpen && (
        <Box
          p='40px'
          color='white'
          mt='4'
          bg='teal.500'
          rounded='md'
          shadow='md'
          // Remove inline styles and use Chakra UI theme or CSS modules
        >
          {/* Placeholder content */}
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
          <p>Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
          {/* Conditionally render chat component (replace with your implementation) */}
          {isOpen && <ChatComponent userText={changeTone()} />}
        </Box>
      )}
    </>
  );
};

// Define your ChatComponent here (replace with actual implementation)
const ChatComponent = ({ userText }) => {
  return (
    <div className="chat-window">
      <p>{userText}</p>
    </div>
  );
};

export default SlideEx;
