import { useState } from 'react';
import { useRef } from 'react'; // Import useRef
import { useDisclosure } from 'react'; // Import useDisclosure
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Button,
  Input,
  Radio,
  RadioGroup,
  Stack,
} from '@chakra-ui/react';

function DrawerWithInputAndRadio() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = useRef();

  const [textValue, setTextValue] = useState('');
  const [radioValue, setRadioValue] = useState('1');

  const handleInputChange = (event) => {
    setTextValue(event.target.value);
  };

  const handleRadioChange = (value) => {
    setRadioValue(value);
  };

  return (
    <>
      <Button ref={btnRef} colorScheme='teal' onClick={onOpen}>
        Open
      </Button>
      <Drawer isOpen={isOpen} placement='right' onClose={onClose} finalFocusRef={btnRef}>
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader>Create your account</DrawerHeader>

          <DrawerBody>
            <Input placeholder='Type here...' value={textValue} onChange={handleInputChange} />
            <RadioGroup onChange={handleRadioChange} value={radioValue}>
              <Stack direction='row'>
                <Radio value='professional'>Professional</Radio>
                <Radio value='funny'>Funny</Radio>
                <Radio value='emotional'>Emotional</Radio>
              </Stack>
            </RadioGroup>
          </DrawerBody>

          <DrawerFooter>
            <Button variant='outline' mr={3} onClick={onClose}>
              Cancel
            </Button>
            <Button colorScheme='blue' onClick={onClose}>
              Save
            </Button>
          </DrawerFooter>
        </DrawerContent>
      </Drawer>
    </>
  );
}

export default DrawerWithInputAndRadio;
